import React from 'react';
import { 
  Clock, 
  PackageSearch, 
  Heart, 
  ClipboardList, 
  TestTube,
  ListChecks,
  FileText,
  FileBarChart,
  Shield,
  Users,
  Wallet,
  LineChart,
  UserCog,
  Table,
  Settings,
  LogOut,
  Menu,
  Search,
  ChevronUp,
  ChevronDown,
  ChevronRight
} from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './Menu.css';

const menuItems = [
  {
    text: "Page",
    type: "header"
  },
  {
    icon: <Clock size={20} />,
    text: "Lịch hẹn",
    path: "/appointments"
  },
  {
    icon: <PackageSearch size={20} />,
    text: "Danh sách thuốc",
    path: "/medicines"
  },
  {
    icon: <Heart size={20} />,
    text: "Hồ sơ bệnh án",
    path: "/medical-records"
  },
  {
    icon: <ClipboardList size={20} />,
    text: "Đơn thuốc",
    path: "/prescriptions"
  },
  {
    icon: <TestTube size={20} />,
    text: "Xét nghiệm",
    path: "/tests"
  },
  {
    icon: <ListChecks size={20} />,
    text: "Lịch sử điều trị",
    path: "/treatment-history"
  },
  {
    text: "Payment",
    type: "header"
  },
  {
    icon: <FileText size={20} />,
    text: "Hóa đơn",
    path: "/invoices"
  },
  {
    icon: <FileBarChart size={20} />,
    text: "Hóa đơn chi tiết",
    path: "/invoice-details",
    active: true
  },
  {
    icon: <Shield size={20} />,
    text: "Bảo hiểm y tế",
    path: "/health-insurance"
  },
  {
    icon: <Users size={20} />,
    text: "Quản lý BHYT chi trả",
    path: "/insurance-payment"
  },
  {
    icon: <Wallet size={20} />,
    text: "Thanh toán",
    path: "/payments"
  },
  {
    icon: <LineChart size={20} />,
    text: "Tổng hợp chi phí",
    path: "/cost-summary"
  },
  {
    text: "System",
    type: "header"
  },
  {
    icon: <UserCog size={20} />,
    text: "Quản lý tài khoản",
    path: "/account"
  },
  {
    icon: <Table size={20} />,
    text: "Table",
    path: "/table"
  },
  {
    icon: <Settings size={20} />,
    text: "Settings",
    path: "/settings"
  },
  {
    icon: <LogOut size={20} />,
    text: "Logout",
    path: "/logout"
  }
];


function StatCard({ title, value, change, icon, iconColor, isPositive }) {
  return (
    <div className="stat-card">
      <div className="stat-icon" style={{ backgroundColor: `${iconColor}15`, color: iconColor }}>
        {icon}
      </div>
      <div className="stat-info">
        <div className="stat-title">{title}</div>
        <div className="stat-value">{value}</div>
        <div className={`stat-change ${isPositive ? 'positive' : 'negative'}`}>
          {isPositive ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
          {change}
        </div>
      </div>
    </div>
  );
}

function SecondaryStatCard({ title, value, change, isPositive }) {
  return (
    <div className="secondary-stat-card">
      <div className="stat-info">
        <div className="stat-title">{title}</div>
        <div className="stat-value">{value}</div>
        <div className={`stat-change ${isPositive ? 'positive' : 'negative'}`}>
          {isPositive ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
          {change}
        </div>
      </div>
    </div>
  );
}

export default function Menu1() {
  return (
    <div className="container">
      {/* Sidebar */}
      <div className="sidebar">
        <div className="sidebar-header">
          <Menu size={20} />
          <div className="brand">
            <span className="brand-blue">Benh</span>
            <span>Vien</span>
          </div>
        </div>

        <nav className="sidebar-nav">
          {menuItems.map((item, index) => (
            item.type === 'header' ? (
              <div key={index} className="nav-header">
                {item.text}
              </div>
            ) : (
              <div 
                key={index} 
                className={`nav-item ${item.active ? 'active' : ''}`}
              >
                {item.icon}
                <span>{item.text}</span>
              </div>
            )
          ))}
        </nav>
      </div>

     
      
    </div>
  );
}
