import React from 'react';
import { 
  Clock, 
  PackageSearch, 
  Heart, 
  ClipboardList, 
  TestTube,
  ListChecks,
  FileText,
  FileBarChart,
  Shield,
  Users,
  Wallet,
  LineChart,
  UserCog,
  Table,
  Settings,
  LogOut,
  Menu,
  Search,
  ChevronUp,
  ChevronDown,
  ChevronRight
} from 'lucide-react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import './Dashboard.css';

const menuItems = [
  {
    text: "Page",
    type: "header"
  },
  {
    icon: <Clock size={20} />,
    text: "Lịch hẹn",
    path: "/appointments"
  },
  {
    icon: <PackageSearch size={20} />,
    text: "Danh sách thuốc",
    path: "/medicines"
  },
  {
    icon: <Heart size={20} />,
    text: "Hồ sơ bệnh án",
    path: "/medical-records"
  },
  {
    icon: <ClipboardList size={20} />,
    text: "Đơn thuốc",
    path: "/prescriptions"
  },
  {
    icon: <TestTube size={20} />,
    text: "Xét nghiệm",
    path: "/tests"
  },
  {
    icon: <ListChecks size={20} />,
    text: "Lịch sử điều trị",
    path: "/treatment-history"
  },
  {
    text: "Payment",
    type: "header"
  },
  {
    icon: <FileText size={20} />,
    text: "Hóa đơn",
    path: "/invoices"
  },
  {
    icon: <FileBarChart size={20} />,
    text: "Hóa đơn chi tiết",
    path: "/invoice-details",
    active: true
  },
  {
    icon: <Shield size={20} />,
    text: "Bảo hiểm y tế",
    path: "/health-insurance"
  },
  {
